  TouchableOpacity,
  Image,
  Linking,
  Alert,
  Platform,
  Animated,
  Easing,
  Dimensions,
} from 'react-native';
import * as Location from 'expo-location';

// Change this to your backend URL
// For testing: use your machine's IP address
const BACKEND_URL = 'http://localhost:5000';

interface Restaurant {
  name: string;
  rating: number | null;
  address: string;
  photoUrl: string | null;
  website: string | null;
  mapsUrl: string | null;
  fsqId: string;
}

interface LocationData {
  latitude: number;
  longitude: number;
}

export default function App() {
  const [location, setLocation] = useState<LocationData | null>(null);
  const [restaurants, setRestaurants] = useState<Restaurant[]>([]);
  const [selectedIndex, setSelectedIndex] = useState<number | null>(null);
  const [selectedRestaurant, setSelectedRestaurant] = useState<Restaurant | null>(null);
  const [loadingList, setLoadingList] = useState(false);
  const [locationLoading, setLocationLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [spinning, setSpinning] = useState(false);
  const [cityLabel, setCityLabel] = useState<string | null>(null);

  const wheelRotation = useRef(new Animated.Value(0)).current; // degrees
  const cumulativeRotation = useRef(0);

  const { width } = Dimensions.get('window');
  const wheelSize = Math.min(320, width - 60);

  // Get user's location on app start
  useEffect(() => {
    getLocation();
  }, []);

  // Fetch restaurants when location changes
  useEffect(() => {
    if (location) {
      fetchRestaurants(location.latitude, location.longitude);
    }
  }, [location]);

  const formatPlace = (city?: string | null, region?: string | null) => {
    const parts = [city, region].filter(Boolean);
    return parts.length > 0 ? parts.join(', ') : 'Current area';
  };

  const setPlaceFromCoords = async (lat: number, lng: number) => {
    try {
      if (Platform.OS !== 'web') {
        const [place] = await Location.reverseGeocodeAsync({ latitude: lat, longitude: lng });
        const city = place?.city || place?.subregion || place?.region || place?.district || null;
        const region = place?.region || place?.country || null;
        setCityLabel(formatPlace(city, region));
      }
    } catch {
      // ignore reverse geocode failures
    }
  };

  /**
   * Get device's GPS location
   */
  const getLocation = async () => {
    try {
      setLocationLoading(true);
      setError(null);
      // Use Expo Location on native platforms
      if (Platform.OS !== 'web') {
        let { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
          setError('Permission to access location was denied');
          setLocationLoading(false);
          // Use NYC as default for testing
          setLocation({ latitude: 40.7128, longitude: -74.0060 });
          return;
        }

        const locationData = await Location.getCurrentPositionAsync({
          accuracy: Location.Accuracy.High,
        });

        const { latitude, longitude } = locationData.coords;
        setLocation({ latitude, longitude });
        setPlaceFromCoords(latitude, longitude);
        setLocationLoading(false);
        return;
      }

      // Web: try browser geolocation first, then fallback to IP-based lookup
      if (Platform.OS === 'web' && typeof navigator !== 'undefined' && 'geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(
          async (pos) => {
            const { latitude, longitude } = pos.coords as any;
            setLocation({ latitude, longitude });
            setLocationLoading(false);
            try {
              const resp = await fetch('https://ipapi.co/json/');
              const j = await resp.json();
              setCityLabel(formatPlace(j.city, j.region));
            } catch {
              setCityLabel('Current area');
            }
          },
          async (err) => {
            console.warn('Browser geolocation failed, falling back to IP lookup', err);
            // fallback to IP-based location service
            try {
              const resp = await fetch('https://ipapi.co/json/');
              const j = await resp.json();
              setLocation({ latitude: j.latitude, longitude: j.longitude });
              setCityLabel(formatPlace(j.city, j.region));
            } catch (e) {
              setLocation({ latitude: 40.7128, longitude: -74.0060 });
              setCityLabel('New York, NY');
            }
            setLocationLoading(false);
          },
          { enableHighAccuracy: true, timeout: 10000 }
        );
        return;
      }
    } catch (err) {
      console.error('Error getting location:', err);
      setError('Failed to get location');
      setLocationLoading(false);
      // Use NYC as default for testing
      setLocation({ latitude: 40.7128, longitude: -74.0060 });
      setCityLabel('New York, NY');
    }
  };

  /**
   * Fetch list of restaurants from backend
   */
  const fetchRestaurants = async (lat: number, lng: number) => {
    try {
      setLoadingList(true);
      setError(null);

      const res = await fetch(`${BACKEND_URL}/api/restaurants`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ latitude: lat, longitude: lng, radius: 5000, limit: 10 }),
      });

      const data = await res.json();

      if (data.success) {
        setRestaurants(data.restaurants || []);
        // clear previous selection
        setSelectedIndex(null);
        setSelectedRestaurant(null);
        cumulativeRotation.current = 0;
        wheelRotation.setValue(0);
      } else {
        setError(data.error || 'Failed to fetch restaurants');
      }
    } catch (err: any) {
      console.error('Error:', err);
      setError(err.message || "Failed to connect to backend. Make sure it's running.");
    } finally {
      setLoadingList(false);
    }
  };

  const openLink = (url: string | null) => {
    if (!url) {
      Alert.alert('Not Available', 'This link is not available');
      return;
    }

    Linking.openURL(url).catch(() => {
      Alert.alert('Error', 'Could not open the link');
    });
  };

  const shortName = (name: string, max = 18) => (name.length > max ? name.slice(0, max - 1) + '...' : name);

  const spinWheel = () => {
    if (spinning || restaurants.length === 0) return;
    setSpinning(true);

    const n = restaurants.length;
    const segment = 360 / n;
    const targetIndex = Math.floor(Math.random() * n);

    // rotate so the center of the target segment lands at top (0deg)
    const segmentCenterAngle = targetIndex * segment + segment / 2;
    const fullTurns = 5; // visual spins

    const targetRotation = fullTurns * 360 + (360 - segmentCenterAngle);

    const start = cumulativeRotation.current;
    const end = start + targetRotation;

    Animated.timing(wheelRotation, {
      toValue: end,
      duration: 4200,
      easing: Easing.out(Easing.cubic),
      useNativeDriver: Platform.OS !== 'web',
    }).start(() => {
      cumulativeRotation.current = end % 360;
      // compute landed index explicitly
      setSelectedIndex(targetIndex);
      setSelectedRestaurant(restaurants[targetIndex]);
      setSpinning(false);
    });
  };

  const rotationInterpolate = wheelRotation.interpolate({
    inputRange: [0, 360],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={styles.container}>
      <View style={styles.bgOrbOne} />
      <View style={styles.bgOrbTwo} />

      <View style={styles.hero}>
        <View style={styles.heroTopRow}>
          <View style={styles.brandBadge}>
            <Text style={styles.brandBadgeText}>SpinDine</Text>
          </View>
          <View style={styles.heroRight}>
            <View style={styles.pill}>
              <Text style={styles.pillText}>
                {restaurants.length > 0 ? `${restaurants.length} nearby` : 'Ready to explore'}
              </Text>
            </View>
            <Text style={styles.cityLabel}>{cityLabel || 'Locating...'}</Text>
          </View>
        </View>
        <Text style={styles.heroTitle}>Let chance pick tonight's table.</Text>
        <Text style={styles.heroSubtitle}>Spin the wheel, discover something new, and go.</Text>
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        <View style={styles.card}>
          <View style={styles.cardHeaderRow}>
            <Text style={styles.cardTitle}>Location</Text>
            <TouchableOpacity style={styles.subtleButton} onPress={getLocation}>
              <Text style={styles.subtleButtonText}>Refresh</Text>
            </TouchableOpacity>
          </View>
          {locationLoading ? (
            <ActivityIndicator size="small" color="#FF6B6B" />
          ) : location ? (
            <>
              <Text style={styles.cardText}>Latitude {location.latitude.toFixed(4)}</Text>
              <Text style={styles.cardText}>Longitude {location.longitude.toFixed(4)}</Text>
            </>
          ) : null}
        </View>

        {error && (
          <View style={[styles.card, styles.errorCard]}>
            <Text style={styles.errorText}>{error}</Text>
          </View>
        )}

        <View style={[styles.card, styles.wheelCard]}>
          <View style={styles.cardHeaderRow}>
            <Text style={styles.cardTitle}>Spin Wheel</Text>
            <Text style={styles.cardHint}>{restaurants.length > 0 ? 'Tap spin' : 'Load options'}</Text>
          </View>

          {loadingList ? (
            <ActivityIndicator size="large" color="#FF6B6B" />
          ) : restaurants.length === 0 ? (
            <Text style={styles.cardText}>No restaurants loaded yet. Refresh location to load nearby options.</Text>
          ) : (
            <View style={{ alignItems: 'center', marginTop: 10 }}>
              <View
                style={{
                  width: wheelSize,
                  height: wheelSize,
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <View style={styles.pointer} />

                <Animated.View
                  style={{
                    transform: [{ rotate: rotationInterpolate }],
                    width: wheelSize,
                    height: wheelSize,
                    alignItems: 'center',
                    justifyContent: 'center',
                  }}
                >
                  <View style={[styles.wheelBase, { width: wheelSize, height: wheelSize, borderRadius: wheelSize / 2 }]}>
                    {restaurants.map((_, i) => {
                      const n = restaurants.length;
                      const angle = (360 / n) * i;
                      const lineLength = wheelSize / 2 - 12;
                      return (
                        <View
                          key={`divider-${i}`}
                          style={[
                            styles.wheelDividerContainer,
                            { width: wheelSize, height: wheelSize, transform: [{ rotate: `${angle}deg` }] },
                          ]}
                          pointerEvents="none"
                        >
                          <View
                            style={[
                              styles.wheelDivider,
                              {
                                height: lineLength,
                                top: wheelSize / 2,
                                left: wheelSize / 2,
                                transform: [{ translateX: -1 }, { translateY: -lineLength }],
                              },
                            ]}
                          />
                        </View>
                      );
                    })}
                    {restaurants.map((r, i) => {
                      const n = restaurants.length;
                      const segment = 360 / n;
                      const angle = segment * i + segment / 2;
                      const rad = (angle * Math.PI) / 180;
                      const labelRadius = wheelSize / 2 - 56;
                      const x = Math.cos(rad) * labelRadius;
                      const y = Math.sin(rad) * labelRadius;
                      return (
                        <View
                          key={r.fsqId || i}
                          style={[
                            styles.wheelLabelWrapper,
                            {
                              left: wheelSize / 2 + x,
                              top: wheelSize / 2 + y,
                              transform: [{ translateX: -40 }, { translateY: -14 }, { rotate: `${angle}deg` }],
                            },
                          ]}
                        >
                          <View style={styles.wheelLabel}>
                            <Text style={styles.wheelLabelText}>{shortName(r.name, 16)}</Text>
                          </View>
                        </View>
                      );
                    })}
                  </View>
                </Animated.View>

                <TouchableOpacity
                  style={[styles.spinButton, { marginTop: 18, width: 230 }]}
                  onPress={spinWheel}
                  disabled={spinning}
                >
                  <Text style={styles.spinButtonText}>{spinning ? 'Spinning...' : 'Spin the wheel'}</Text>
                </TouchableOpacity>

                {selectedRestaurant && (
                  <View style={[styles.resultCard, { width: Math.min(420, width - 40), marginTop: 20 }]}>
                    {selectedRestaurant.photoUrl && (
                      <Image source={{ uri: selectedRestaurant.photoUrl }} style={styles.restaurantPhoto} />
                    )}
                    <Text style={styles.restaurantName}>{selectedRestaurant.name}</Text>
                    {selectedRestaurant.rating && (
                      <Text style={styles.rating}>Rating {selectedRestaurant.rating.toFixed(1)}/10</Text>
                    )}
                    <Text style={styles.address}>{selectedRestaurant.address}</Text>
                    <View style={styles.buttonGroup}>
                      {selectedRestaurant.mapsUrl && (
                        <TouchableOpacity style={[styles.actionButton, styles.mapsButton]} onPress={() => openLink(selectedRestaurant.mapsUrl)}>
                          <Text style={styles.actionButtonText}>Open Maps</Text>
                        </TouchableOpacity>
                      )}
                      {selectedRestaurant.website && (
                        <TouchableOpacity style={[styles.actionButton, styles.websiteButton]} onPress={() => openLink(selectedRestaurant.website)}>
                          <Text style={styles.actionButtonText}>Website</Text>
                        </TouchableOpacity>
                      )}
                    </View>
                  </View>
                )}
              </View>
            </View>
          )}
        </View>

        <View style={styles.spacer} />
      </ScrollView>
    </View>
  );
}

