import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ActivityIndicator,
  ScrollView,
  TouchableOpacity,
  Image,
  Linking,
  Alert,
  Platform,
  Animated,
  Easing,
  Dimensions,
} from 'react-native';
import * as Location from 'expo-location';

// Change this to your backend URL
// For testing: use your machine's IP address
const BACKEND_URL = 'http://localhost:5000';

interface Restaurant {
  name: string;
  rating: number | null;
  address: string;
  photoUrl: string | null;
  website: string | null;
  mapsUrl: string | null;
  fsqId: string;
}

interface LocationData {
  latitude: number;
  longitude: number;
}

export default function App() {
  const [location, setLocation] = useState<LocationData | null>(null);
  const [restaurants, setRestaurants] = useState<Restaurant[]>([]);
  const [selectedIndex, setSelectedIndex] = useState<number | null>(null);
  const [selectedRestaurant, setSelectedRestaurant] = useState<Restaurant | null>(null);
  const [loadingList, setLoadingList] = useState(false);
  const [locationLoading, setLocationLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [spinning, setSpinning] = useState(false);
  const [cityLabel, setCityLabel] = useState<string | null>(null);

  const wheelRotation = useRef(new Animated.Value(0)).current;
  const cumulativeRotation = useRef(0);
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(30)).current;

  const { width } = Dimensions.get('window');
  const wheelSize = Math.min(340, width - 80);

  useEffect(() => {
    getLocation();
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 600,
        easing: Easing.out(Easing.cubic),
        useNativeDriver: true,
      }),
    ]).start();
  }, []);

  useEffect(() => {
    if (location) {
      fetchRestaurants(location.latitude, location.longitude);
    }
  }, [location]);

  const formatPlace = (city?: string | null, region?: string | null) => {
    const parts = [city, region].filter(Boolean);
    return parts.length > 0 ? parts.join(', ') : 'Current area';
  };

  const setPlaceFromCoords = async (lat: number, lng: number) => {
    try {
      if (Platform.OS !== 'web') {
        const [place] = await Location.reverseGeocodeAsync({ latitude: lat, longitude: lng });
        const city = place?.city || place?.subregion || place?.region || place?.district || null;
        const region = place?.region || place?.country || null;
        setCityLabel(formatPlace(city, region));
      }
    } catch {
      // ignore reverse geocode failures
    }
  };

  const getLocation = async () => {
    try {
      setLocationLoading(true);
      setError(null);
      if (Platform.OS !== 'web') {
        let { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
          setError('Permission to access location was denied');
          setLocationLoading(false);
          setLocation({ latitude: 40.7128, longitude: -74.006 });
          return;
        }

        const locationData = await Location.getCurrentPositionAsync({
          accuracy: Location.Accuracy.High,
        });

        const { latitude, longitude } = locationData.coords;
        setLocation({ latitude, longitude });
        setPlaceFromCoords(latitude, longitude);
        setLocationLoading(false);
        return;
      }

      if (Platform.OS === 'web' && typeof navigator !== 'undefined' && 'geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(
          async (pos) => {
            const { latitude, longitude } = pos.coords as any;
            setLocation({ latitude, longitude });
            setLocationLoading(false);
            try {
              const resp = await fetch('https://ipapi.co/json/');
              const j = await resp.json();
              setCityLabel(formatPlace(j.city, j.region));
            } catch {
              setCityLabel('Current area');
            }
          },
          async (err) => {
            console.warn('Browser geolocation failed, falling back to IP lookup', err);
            try {
              const resp = await fetch('https://ipapi.co/json/');
              const j = await resp.json();
              setLocation({ latitude: j.latitude, longitude: j.longitude });
              setCityLabel(formatPlace(j.city, j.region));
            } catch (e) {
              setLocation({ latitude: 40.7128, longitude: -74.006 });
              setCityLabel('New York, NY');
            }
            setLocationLoading(false);
          },
          { enableHighAccuracy: true, timeout: 10000 }
        );
        return;
      }
    } catch (err) {
      console.error('Error getting location:', err);
      setError('Failed to get location');
      setLocationLoading(false);
      setLocation({ latitude: 40.7128, longitude: -74.006 });
      setCityLabel('New York, NY');
    }
  };

  const fetchRestaurants = async (lat: number, lng: number) => {
    try {
      setLoadingList(true);
      setError(null);

      const res = await fetch(`${BACKEND_URL}/api/restaurants`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ latitude: lat, longitude: lng, radius: 5000, limit: 10 }),
      });

      const data = await res.json();

      if (data.success) {
        setRestaurants(data.restaurants || []);
        setSelectedIndex(null);
        setSelectedRestaurant(null);
        cumulativeRotation.current = 0;
        wheelRotation.setValue(0);
      } else {
        setError(data.error || 'Failed to fetch restaurants');
      }
    } catch (err: any) {
      console.error('Error:', err);
      setError(err.message || "Failed to connect to backend. Make sure it's running.");
    } finally {
      setLoadingList(false);
    }
  };

  const openLink = (url: string | null) => {
    if (!url) {
      Alert.alert('Not Available', 'This link is not available');
      return;
    }
    Linking.openURL(url).catch(() => {
      Alert.alert('Error', 'Could not open the link');
    });
  };

  const shortName = (name: string, max = 14) => (name.length > max ? name.slice(0, max - 1) + '‚Ä¶' : name);

  const spinWheel = () => {
    if (spinning || restaurants.length === 0) return;
    setSpinning(true);

    const n = restaurants.length;
    const segment = 360 / n;
    const targetIndex = Math.floor(Math.random() * n);

    const segmentCenterAngle = targetIndex * segment + segment / 2;
    const fullTurns = 5;

    const targetRotation = fullTurns * 360 + (360 - segmentCenterAngle);

    const start = cumulativeRotation.current;
    const end = start + targetRotation;

    Animated.timing(wheelRotation, {
      toValue: end,
      duration: 4000,
      easing: Easing.out(Easing.cubic),
      useNativeDriver: Platform.OS !== 'web',
    }).start(() => {
      cumulativeRotation.current = end % 360;
      setSelectedIndex(targetIndex);
      setSelectedRestaurant(restaurants[targetIndex]);
      setSpinning(false);
    });
  };

  const rotationInterpolate = wheelRotation.interpolate({
    inputRange: [0, 360],
    outputRange: ['0deg', '360deg'],
  });

  const colors = [
    '#FF6B6B',
    '#4ECDC4',
    '#45B7D1',
    '#FFA07A',
    '#98D8C8',
    '#F7DC6F',
    '#BB8FCE',
    '#85C1E2',
    '#F8B195',
    '#C06C84',
  ];

  return (
    <View style={styles.container}>
      <View style={styles.gradientBg}>
        <View style={styles.bgBlob1} />
        <View style={styles.bgBlob2} />
        <View style={styles.bgBlob3} />
      </View>

      <Animated.View
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        <ScrollView showsVerticalScrollIndicator={false} contentContainerStyle={styles.scrollContent}>
          {/* Header */}
          <View style={styles.header}>
            <View style={styles.headerTop}>
              <View style={styles.brandContainer}>
                <View style={styles.logoCircle}>
                  <Text style={styles.logoEmoji}>üé°</Text>
                </View>
                <Text style={styles.brandText}>SpinDine</Text>
              </View>
              <TouchableOpacity style={styles.locationPill} onPress={getLocation}>
                <View style={styles.locationDot} />
                <Text style={styles.locationText}>{cityLabel || 'Locating...'}</Text>
              </TouchableOpacity>
            </View>

            <View style={styles.heroContent}>
              <Text style={styles.heroTitle}>Where should{'\n'}we eat tonight?</Text>
              <Text style={styles.heroSubtitle}>
                Spin the wheel and let fate decide your next culinary adventure
              </Text>
              {restaurants.length > 0 && (
                <View style={styles.statsContainer}>
                  <View style={styles.statBadge}>
                    <Text style={styles.statNumber}>{restaurants.length}</Text>
                    <Text style={styles.statLabel}>options nearby</Text>
                  </View>
                </View>
              )}
            </View>
          </View>

          {error && (
            <View style={styles.errorBanner}>
              <Text style={styles.errorIcon}>‚ö†Ô∏è</Text>
              <Text style={styles.errorText}>{error}</Text>
            </View>
          )}

          {/* Wheel Section */}
          <View style={styles.wheelSection}>
            {loadingList ? (
              <View style={styles.loadingContainer}>
                <ActivityIndicator size="large" color="#FF6B6B" />
                <Text style={styles.loadingText}>Finding amazing places...</Text>
              </View>
            ) : restaurants.length === 0 ? (
              <View style={styles.emptyState}>
                <Text style={styles.emptyEmoji}>üçΩÔ∏è</Text>
                <Text style={styles.emptyTitle}>Ready to explore</Text>
                <Text style={styles.emptyText}>
                  Tap the location button above to load nearby restaurants
                </Text>
              </View>
            ) : (
              <View style={styles.wheelContainer}>
                <View style={styles.wheelWrapper}>
                  {/* Pointer */}
                  <View style={styles.pointerContainer}>
                    <View style={styles.pointer} />
                  </View>

                  {/* Wheel */}
                  <Animated.View
                    style={[
                      styles.wheelAnimated,
                      {
                        transform: [{ rotate: rotationInterpolate }],
                        width: wheelSize,
                        height: wheelSize,
                      },
                    ]}
                  >
                    <View
                      style={[
                        styles.wheel,
                        {
                          width: wheelSize,
                          height: wheelSize,
                          borderRadius: wheelSize / 2,
                        },
                      ]}
                    >
                      {/* Segments */}
                      {restaurants.map((_, i) => {
                        const n = restaurants.length;
                        const segment = 360 / n;
                        const startAngle = i * segment;
                        return (
                          <View
                            key={`segment-${i}`}
                            style={[
                              styles.segment,
                              {
                                width: wheelSize,
                                height: wheelSize,
                                transform: [{ rotate: `${startAngle}deg` }],
                              },
                            ]}
                          >
                            <View
                              style={[
                                styles.segmentFill,
                                {
                                  backgroundColor: colors[i % colors.length],
                                  width: wheelSize / 2,
                                  height: wheelSize,
                                  borderTopRightRadius: segment <= 180 ? wheelSize / 2 : 0,
                                  borderBottomRightRadius: segment <= 180 ? wheelSize / 2 : 0,
                                  transform: [
                                    { translateX: wheelSize / 2 },
                                    { rotate: `${segment}deg` },
                                    { translateX: -wheelSize / 2 },
                                  ],
                                },
                              ]}
                            />
                          </View>
                        );
                      })}

                      {/* Labels */}
                      {restaurants.map((r, i) => {
                        const n = restaurants.length;
                        const segment = 360 / n;
                        const angle = segment * i + segment / 2;
                        const rad = (angle * Math.PI) / 180;
                        const labelRadius = wheelSize / 2 - 45;
                        const x = Math.cos(rad) * labelRadius;
                        const y = Math.sin(rad) * labelRadius;
                        return (
                          <View
                            key={r.fsqId || i}
                            style={[
                              styles.labelWrapper,
                              {
                                left: wheelSize / 2 + x,
                                top: wheelSize / 2 + y,
                                transform: [
                                  { translateX: -50 },
                                  { translateY: -12 },
                                  { rotate: `${angle}deg` },
                                ],
                              },
                            ]}
                          >
                            <View style={styles.label}>
                              <Text style={styles.labelText} numberOfLines={1}>
                                {shortName(r.name)}
                              </Text>
                            </View>
                          </View>
                        );
                      })}

                      {/* Center Circle */}
                      <View style={styles.centerCircle}>
                        <Text style={styles.centerIcon}>üé≤</Text>
                      </View>
                    </View>
                  </Animated.View>
                </View>

                {/* Spin Button */}
                <TouchableOpacity
                  style={[styles.spinButton, spinning && styles.spinButtonDisabled]}
                  onPress={spinWheel}
                  disabled={spinning}
                  activeOpacity={0.8}
                >
                  <View style={styles.spinButtonGlow} />
                  <Text style={styles.spinButtonText}>{spinning ? 'üé° Spinning...' : 'üéØ Spin the Wheel'}</Text>
                </TouchableOpacity>

                {/* Result Card */}
                {selectedRestaurant && (
                  <Animated.View style={styles.resultCard}>
                    <View style={styles.resultHeader}>
                      <Text style={styles.resultBadge}>üéâ Your Pick!</Text>
                    </View>

                    {selectedRestaurant.photoUrl && (
                      <View style={styles.photoContainer}>
                        <Image
                          source={{ uri: selectedRestaurant.photoUrl }}
                          style={styles.restaurantPhoto}
                          resizeMode="cover"
                        />
                        <View style={styles.photoOverlay} />
                      </View>
                    )}

                    <View style={styles.resultContent}>
                      <Text style={styles.restaurantName}>{selectedRestaurant.name}</Text>

                      {selectedRestaurant.rating && (
                        <View style={styles.ratingContainer}>
                          <Text style={styles.ratingStars}>‚≠êÔ∏è</Text>
                          <Text style={styles.ratingText}>{selectedRestaurant.rating.toFixed(1)}</Text>
                          <Text style={styles.ratingMax}>/10</Text>
                        </View>
                      )}

                      <View style={styles.addressContainer}>
                        <Text style={styles.addressIcon}>üìç</Text>
                        <Text style={styles.addressText}>{selectedRestaurant.address}</Text>
                      </View>

                      <View style={styles.actionButtons}>
                        {selectedRestaurant.mapsUrl && (
                          <TouchableOpacity
                            style={[styles.actionButton, styles.primaryButton]}
                            onPress={() => openLink(selectedRestaurant.mapsUrl)}
                            activeOpacity={0.8}
                          >
                            <Text style={styles.actionButtonText}>üó∫Ô∏è Get Directions</Text>
                          </TouchableOpacity>
                        )}
                        {selectedRestaurant.website && (
                          <TouchableOpacity
                            style={[styles.actionButton, styles.secondaryButton]}
                            onPress={() => openLink(selectedRestaurant.website)}
                            activeOpacity={0.8}
                          >
                            <Text style={styles.secondaryButtonText}>üåê Visit Website</Text>
                          </TouchableOpacity>
                        )}
                      </View>
                    </View>
                  </Animated.View>
                )}
              </View>
            )}
          </View>

          <View style={styles.footer}>
            <Text style={styles.footerText}>Made with ‚ù§Ô∏è for food lovers</Text>
          </View>
        </ScrollView>
      </Animated.View>
    </View>
  );
}

